-- Table structure for goods
-- ----------------------------
DROP TABLE IF EXISTS `goods`;
CREATE TABLE `goods` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `supplier_id` int(10) unsigned NOT NULL DEFAULT '0',
  `brand_id` int(10) unsigned NOT NULL DEFAULT '0',
  `category_id` int(10) unsigned NOT NULL DEFAULT '0',
  `area_id` int(11) DEFAULT NULL,
  `series_id` int(11) unsigned NOT NULL DEFAULT '0',
  `style_id` int(11) unsigned NOT NULL DEFAULT '0',
  `sku` bigint(20) unsigned NOT NULL DEFAULT '0',
  `title` varchar(100) NOT NULL DEFAULT '',
  `subtitle` varchar(100) NOT NULL DEFAULT '',
  `cover_image` varchar(255) NOT NULL DEFAULT '' COMMENT '封面图',
  `supplier_price` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `platform_price` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `market_price` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `purchase_price_decoration_company` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `purchase_price_manager` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `purchase_price_designer` bigint(20) NOT NULL DEFAULT '0' COMMENT 'unit: fen',
  `sold_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '销量',
  `left_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '销量',
  `comment_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '评价数',
  `viewed_number` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览量',
  `favourable_comment_rate` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '好评率',
  `description` varchar(255) NOT NULL DEFAULT '',
  `create_time` int(11) NOT NULL DEFAULT '0',
  `offline_time` int(11) NOT NULL DEFAULT '0',
  `online_time` int(11) NOT NULL DEFAULT '0',
  `delete_time` int(11) NOT NULL DEFAULT '0',
  `publish_time` int(11) NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0：已下架, 1：等待上架, 2：已上架, 3：已删除',
  `after_sale_services` set('0','1','2','3','4') NOT NULL DEFAULT '0' COMMENT '0：提供发票, 1：上门安装, 2：上门维修, 3：上门退货, 4:上门换货, 5：退货, 6:换货',
  `logistics_template_id` int(11) unsigned NOT NULL DEFAULT '0',
  `offline_reason` varchar(100) NOT NULL DEFAULT '' COMMENT '下架原因',
  `offline_person` varchar(20) NOT NULL DEFAULT '' COMMENT '下架人',
  `offline_uid` int(11) unsigned NOT NULL DEFAULT '0',
  `reason` varchar(100) NOT NULL DEFAULT '' COMMENT '审核原因',
  `online_person` varchar(20) NOT NULL DEFAULT '' COMMENT '上架人',
  `online_uid` int(11) unsigned NOT NULL DEFAULT '0',
  `profit_rate` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '利润率',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=197 DEFAULT CHARSET=utf8;

